// For mutliple bills
const getMutlipleBillsData = (transformedDataArray) => {
  let multipleBillData = transformedDataArray.map((transformedData, index) => {
    return getPdfContent(transformedData, transformedDataArray.length, index);
  });
  let finalMultipleBillData = {
    content: multipleBillData,
    styles: stylesForBills,
  };
  return finalMultipleBillData;
};
//generateMutlipleBills PDF
export const generateMultipleBill = async (state, dispatch, type) => {
  dispatch(toggleSpinner());
  let allBills = get(
    state.screenConfiguration,
    "preparedFinalObject.searchScreenMdmsData.billSearchResponse",
    []
  );
  const commonPayDetails = get(
    state.screenConfiguration,
    "preparedFinalObject.searchScreenMdmsData.common-masters.uiCommonPay",
    []
  );
  const businessService = get(
    state.screenConfiguration,
    "preparedFinalObject.searchCriteria.businesService",
    ""
  );

  let billkey = "";
  const index =
    commonPayDetails &&
    commonPayDetails.findIndex((item) => {
      return item.code == businessService;
    });
  if (index > -1) {
    billkey = get(commonPayDetails[index], "billKey", "");
  } else {
    const details =
      commonPayDetails &&
      commonPayDetails.filter((item) => item.code === "DEFAULT");
    billkey = get(details, "billKey", "");
  }
  allBills = allBills.filter((bill) => bill.status === "ACTIVE");
  allBills &&
    allBills.length > 0 &&
    (await downloadMultipleBill(allBills, billkey));
  /* 
  To Download Files based on Filestoreid logic
  
  let filestoreids=[];
  let bills=[];
  allBills.map(bill=>{
    if(bill.status==='ACTIVE'){
      if(bill.fileStoreId==null){
        bills.push(bill);
      }else{
        filestoreids.push(bill.fileStoreId)
    }
    }
  })
  bills&&bills.length>0&&await downloadMultipleBill(bills,billkey);
  filestoreids&&filestoreids.length>0&&downloadMultipleFileFromFilestoreIds(filestoreids,'download'); */
  dispatch(toggleSpinner());
};

export const downloadMultipleBills = async (
  bills = [],
  configKey,
  tenantId,
  locality,
  isConsolidated,
  businesService,
  consumerCode,
  dispatch
) => {
  try {
    const DOWNLOADRECEIPT = {
      GET: {
        URL: "/egov-pdf/download/WNS/wnsgroupbill",
        ACTION: "_get",
      },
    };
    const queryStr = [
      { key: "key", value: configKey },
      { key: "tenantId", value: tenantId || commonConfig.tenantId },
      { key: "locality", value: locality },
      { key: "isConsolidated", value: isConsolidated },
    ];
    if (businesService) {
      queryStr.push({ key: "bussinessService", value: businesService });
    }
    if (consumerCode) {
      queryStr.push({ key: "consumerCode", value: consumerCode });
    }
    /*
to download all bulk pdf directly egov-pdf
     downloadPdfFile(DOWNLOADRECEIPT.GET.URL,'post',queryStr,{},{},false, `${businesService}_${locality}.pdf`); //{ Bill: bills }
  */
    const resp = await httpRequest(
      "post",
      DOWNLOADRECEIPT.GET.URL,
      "create",
      queryStr
    );
    if (resp) {
      dispatch(
        toggleSnackbar(
          true,
          {
            labelName: "JOB_INITIATED_STATUS",
            labelKey: `${getLocaleLabels(
              "GRP_JOB_INITIATED_STATUS",
              "GRP_JOB_INITIATED_STATUS"
            )} ${resp.jobId}`,
          },
          "success"
        )
      );
      // window.location.href.includes("abg/billDownload")&&window.location.reload();
    }
    
  } catch (error) {
    console.error(error);
    dispatch(toggleSnackbar(true, error.message, "error"));
  }
};

export const cancelGeneratedJob = async (
  dispatch,
  jobid
) => {
  try {
    const CANCEL = {
      GET: {
        URL: "/pdf-service/v1/_cancelProcess",
        ACTION: "_cancel",
      },
    };
    const queryStr = [
      { key: "jobId", value: jobid },
    ];
  
    const resp = await httpRequest(
      "post",
      CANCEL.GET.URL,
      "create",
      queryStr
    );
    if (resp) {
      dispatch(
        toggleSnackbar(
          true,
          {
            labelName: "JOB_CANCELLED_STATUS",
            labelKey: `${getLocaleLabels(
              "GRP_JOB_CANCELLED_STATUS",
              "GRP_JOB_CANCELLED_STATUS"
            )} ${jobid}`,
          },
          "success"
        )
      );
      // window.location.href.includes("abg/billDownload")&&window.location.reload();
    }
  } catch (error) {
    console.error(error);
    dispatch(toggleSnackbar(true, error.message, "error"));
  }
};

//generateMutlipleBills PDF
export const generateMultipleBills = async (
  state,
  dispatch,
  tenantId,
  locality,
  isConsolidated,
  businesService,
  consumerCode
) => {
  dispatch(toggleSpinner());
  let allBills = get(
    state.screenConfiguration,
    "preparedFinalObject.searchScreenMdmsData.billSearchResponse",
    []
  );
  const commonPayDetails = get(
    state.screenConfiguration,
    "preparedFinalObject.searchScreenMdmsData.common-masters.uiCommonPay",
    []
  );
  const businessService = get(
    state.screenConfiguration,
    "preparedFinalObject.searchCriteria.businesService",
    ""
  );

  let billkey = "";
  const index =
    commonPayDetails &&
    commonPayDetails.findIndex((item) => {
      return item.code == businessService;
    });
  if (index > -1) {
    billkey = get(commonPayDetails[index], "billKey", "");
  } else {
    const details =
      commonPayDetails &&
      commonPayDetails.filter((item) => item.code === "DEFAULT");
    billkey = get(details, "billKey", "");
  }
  allBills = allBills.filter((bill) => bill.status === "ACTIVE");
  allBills &&
    allBills.length > 0 &&
    (await downloadMultipleBills(
      allBills,
      billkey,
      tenantId,
      locality,
      isConsolidated,
      businesService,
      consumerCode,
      dispatch
    ));
  dispatch(toggleSpinner());
};

